buildscript {    repositories {        addRepositories()    }    dependencies {        classpath("com.android.tools.build:gradle:7.0.0")        classpath("org.jetbrains.kotlin:kotlin-gradle-plugin:${Version.kotlin}")        classpath("androidx.navigation:navigation-safe-args-gradle-plugin:2.3.5")    }}allprojects {    repositories {        addRepositories()    }}task<Delete>("clean") {    delete(rootProject.buildDir)}